import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";



const SearchBar=()=>{
     const search=useInput()
     const [result, setResult]=useState([])

     const submitHandler=(e)=>{
         e.preventDefault()
         axios.get(`api/movies/:${search.value}`)
        .then((r)=>setResult(r.data))
        .catch((err)=>console.log(err))
     }

    return(
        <>
      <div className="notification is-primary">
        <div className="field has-addons">
        <div className="control">
          <input {...search} className="input search" type="text" placeholder="Find a Movie" />
        </div>
        <div className="control">
          <a className="button is-info" onClick={submitHandler}>Search</a>
        </div>
      </div>
      <div class="control">
  <label class="radio">
    <input type="radio" name="answer"/>
    Movie
  </label>
  <label class="radio">
    <input type="radio" name="answer"/>
    No
  </label>
</div>
      </div>
      <MovieListItem movies={result}/>
      </>

    )
}

export default SearchBar
