import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";



const SearchBar=()=>{
     const search=useInput()
     const [result, setResult]=useState([])
     const [media,setMedia]=useState('')

     const submitHandler=(e)=>{
         e.preventDefault()
         axios.get(`api/movies/$/:${search.value}`)
        .then((r)=>setResult(r.data))
        .catch((err)=>console.log(err))
     }

     const clickHanlder=(e)=>{
       setMedia(e.target.value)
      }
      console.log(media)

    return(
        <>
      <div className="notification is-primary">
        <div className="field has-addons">
        <div className="control">
          <input {...search} className="input search" type="text" placeholder={`Find a ...`} />
        </div>
        <div className="control">
          <a className="button is-info" onClick={submitHandler}>Search</a>
        </div>
      </div>
      <div class="control">
  <label class="radio">
    <input onClick={clickHanlder} type="radio" name="answer" value={'movie'}/>
    Movie
  </label>
  <label class="radio">
    <input onClick={clickHanlder} type="radio" name="answer" value={'tv'}/>
    Tv Show
  </label>
  <label class="radio">
    <input onClick={clickHanlder} type="radio" name="answer" value={'person'}/>
    Person
  </label>
</div>
      </div>
      <MovieListItem movies={result}/>
      </>

    )
}

export default SearchBar
