import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";
import { useNavigate } from "react-router";



const SearchBar=()=>{
    const navigate=useNavigate()
     const search=useInput()
     const [result, setResult]=useState([])
     const [media,setMedia]=useState('')

     const submitHandler=(e)=>{
         e.preventDefault()

         axios.get(`api/movies/${media}/:${search.value}`)
        .then((r)=>setResult(r.data))
        .catch((err)=>navigate('/404'))
     }

     const clickHanlder=(e)=>{
       setMedia(e.target.value)
      }


    return(
        <form onClick={submitHandler}>
      <div className="notification is-primary">
        <div className="field has-addons">
        <div className="control">
          <input {...search} className="input search" type="text" placeholder={`Find a ${media}...`} />
          {!media && <strong class="help">Please select a category.</strong>}
        </div>
        <div className="control">
          <a className="button is-info" >Search</a>
        </div>
      </div>
      <div class="control">
  <label class="radio">
    <input onClick={clickHanlder} type="radio" name="answer" value={'movie'}/>
    Movie
  </label>
  <label class="radio">
    <input onClick={clickHanlder} type="radio" name="answer" value={'tv'}/>
    Tv Show
  </label>
  <label class="radio">
    <input onClick={clickHanlder} type="radio" name="answer" value={'user'}/>
    User
  </label>
</div>
      </div>
      <MovieListItem movies={result} />
      </form>

    )
}

export default SearchBar
