import axios from "axios";
import React, { useEffect, useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";
import { useNavigate } from "react-router";
import UserProfile from "../components/UserProfile";
import res from "express/lib/response";

const SearchBar = () => {
  const navigate = useNavigate();
  const search = useInput();
  const [result, setResult] = useState([]);
  const [media, setMedia] = useState("");

useEffect(()=> 
axios.get("/api/movies/")
.then((r)=>setResult(r.data))
.catch((err)=>console)
,[])

  const submitHandler = (e) => {
    e.preventDefault();
    if (media === "users") {
      axios
        .get(`/api/users/search/:${search.value}`)
        .then((r) =>
          r.data.length === 0 ? navigate("/404") : setResult(r.data)
        )
        .catch((err) => navigate("/404"));
    } else {
      axios
        .get(`/api/movies/${media}/:${search.value}`)
        .then((r) =>
          r.data.length === 0 ? navigate("/404") : setResult(r.data)
        )
        .catch((err) => navigate("/404"));
    }
  };


  const clickHanlder = (e) => {
    setMedia(e.target.value);
  };

  return (
    <div >
      <form onSubmit={submitHandler}>
        <div className="notification is-primary">
          <div className="field is-horizontal has-addons ">
            <div className="control search">
              <input
                {...search}
                className="input is-hovered "
                type="text"
                placeholder={`Find a ${media}...`}
              />
              {!media && (
                <strong className="help">Please select a category.</strong>
              )}
            </div>
            <div className="control">
              <button
                disabled={!media && "true"}
                type="submit"
                className="button is-info"
              >
                Search
              </button>
            </div>
          </div>
          <div className="control">
            <label className="radio">
              <input
                onClick={clickHanlder}
                type="radio"
                name="answer"
                value={"movie"}
              />
              Movie
            </label>
            <label className="radio">
              <input
                onClick={clickHanlder}
                type="radio"
                name="answer"
                value={"tv"}
              />
              Tv Show
            </label>
            <label className="radio">
              <input
                onClick={clickHanlder}
                type="radio"
                name="answer"
                value={"users"}
              />
              Users
            </label>
          </div>
        </div>
      </form>
      {Array.isArray(result) ? (
        <MovieListItem movies={result} />
      ) : (
        <UserProfile user={result} />
      )}
    </div>
  );
};

export default SearchBar;
