import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";
import { useNavigate } from "react-router";
import { disabled } from "express/lib/application";

const SearchBar = () => {
  const navigate = useNavigate();
  const search = useInput();
  const [result, setResult] = useState([]);
  const [media, setMedia] = useState("");

  const submitHandler = (e) => {
    e.preventDefault();
    if (media === "users") {
      axios
        .get(`/api/users/search/:${search.value}`)
        .then((r) => console.log(r.data))
        .catch((err) => navigate("/404"));
    } else {
      axios
        .get(`/api/movies/${media}/:${search.value}`)
        .then((r) => setResult(r.data))
        .catch((err) => navigate("/404"));
    }
  };

  const clickHanlder = (e) => {
    setMedia(e.target.value);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="notification is-primary">
        <div className="field has-addons">
          <div className="control">
            <input
              onChange={search.onChange}
              className="input search"
              type="text"
              placeholder={`Find a ${media}...`}
            />
            {!media && <strong class="help">Please select a category.</strong>}
          </div>
          <div className="control">
            <button
              disabled={!media && "true"}
              type="submit"
              className="button is-info"
            >
              Search
            </button>
          </div>
        </div>
        <div class="control">
          <label class="radio">
            <input
              onClick={clickHanlder}
              type="radio"
              name="answer"
              value={"movie"}
            />
            Movie
          </label>
          <label class="radio">
            <input
              onClick={clickHanlder}
              type="radio"
              name="answer"
              value={"tv"}
            />
            Tv Show
          </label>
          <label class="radio">
            <input
              onClick={clickHanlder}
              type="radio"
              name="answer"
              value={"users"}
            />
            Users
          </label>
        </div>
      </div>
      <MovieListItem movies={result} />
    </form>
  );
};

export default SearchBar;
