import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import { FaHeart, FaTrash, FaStar } from "react-icons/fa";
import { AuthContext } from "../store/context/AuthContext";
import axios from "axios";

const MovieItem = ({ movie, setFavs }) => {
  const { user } = useContext(AuthContext);
  const { userid } = useParams();

  const addToFavourites = (item) => {
    axios
      .put(`/api/users/${user.id}/add_favourites`, item)
      .then((result) => {
        alert("Added to your collection successfully");
        setFavs(result.data);
      })
      .catch((err) => console.log(err));
  };

  const deleteFromFavourites = (item) => {
    axios
      .put(`/api/users/${user.id}/delete_favourites`, item)
      .then((result) => setFavs(result.data))
      .catch((err) => console.log(err));
  };

  return (
    <div className="card" sty>
      <div className="card-image">
        <figure className="has-ratio">
          <img
            src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
            alt="Placeholder image"
          />
        </figure>
      </div>
      <div className="card-content">
        <div className="media">
          <div className="media-left">
            {user && <FaHeart onClick={() => addToFavourites(movie)} />}
          </div>
          <div className="media-content">
            <p className="title is-4">{movie.original_title}</p>
          </div>
          <p className="subtitle is-6">by Tomi</p>
          <div className="media-right">
            {userid && <FaTrash onClick={() => deleteFromFavourites(movie)} />}
          </div>
        </div>
        <div className="content">
          {movie.overview}
          <Link
            to={`/single/${movie.first_air_date ? "tv" : "movie"}/${movie.id}`}
          >
            {" "}
            More
          </Link>
          <br />
          <strong>Rank: </strong> <>{movie.vote_average} <FaStar/></>
          <br />
          <time dateTime="2016-1-1">
            <strong>Release Date: </strong>
            {movie.release_date}
          </time>
        </div>
      </div>
    </div>
  );
};

export default MovieItem;
