import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../store/context/AuthContext";
import { useNavigate } from "react-router";


const Navbar = () => {
const navigate=useNavigate()
  const { isAuthenticated, toggleAuth, user } = useContext(AuthContext);
  const clickHandler = () => {
    toggleAuth(null);
    navigate('/')

  };

  return (
    <nav className="navbar the" role="navigation" aria-label="main navigation">
      <div className="navbar-brand">
        <Link to="/">
          <img
            className="navbar-item"
            width={"75%"}
            src="https://www.themoviedb.org/assets/2/v4/logos/v2/blue_long_1-8ba2ac31f354005783fab473602c34c3f4fd207150182061e425d366e4f34596.svg"
            alt="notfound"
          />
        </Link>

        <a
          role="button"
          className="navbar-burger"
          aria-label="menu"
          aria-expanded="false"
          data-target="navbarBasicExample"
        >
          <span aria-hidden="true"></span>
          <span aria-hidden="true"></span>
          <span aria-hidden="true"></span>
        </a>
      </div>

      <div id="navbarBasicExample" className="navbar-menu">
        <div className="navbar-start">

          {user&& <Link to={`/favourites/${user.id}`} className="navbar-item basefont">Your collection</Link>}


        </div>

        <div className="navbar-end">
          <div className="navbar-item">
            <div className="buttons">
              {isAuthenticated ? (
                <>
                  <Link to={`/your_profile/${user.id}`}>
                    <strong className="basefont">{user.name}</strong>
                  </Link>
                  <a onClick={clickHandler} className="button is-info">
                    Log Out
                  </a>
                </>
              ) : (
                <>
                  <Link to="/signUp" className="button data is-primary">
                    <strong>Sign up</strong>
                  </Link>
                  <Link to="/logIn" className="base button is-light ">
                    Log in
                  </Link>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
