import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";
import { useNavigate } from "react-router";
import UserProfile from "../components/UserProfile";

const SearchBar = () => {
  const navigate = useNavigate();
  const search = useInput();
  const [result, setResult] = useState([]);
  const [media, setMedia] = useState("");

  const submitHandler = (e) => {
    e.preventDefault();
    if (media === "users") {
      axios.get(`/api/users/search/:${search.value}`)
        .then((r) => setResult(r.data))
        .catch((err) => navigate("/404"))
    } else {
      axios.get(`/api/movies/${media}/:${search.value}`)
        .then((r) => setResult(r.data))
        .catch((err) => navigate("/404"));
    }
  };

  const clickHanlder = (e) => {
    setMedia(e.target.value);
  };

  return (
    <>
    <div className="columns">
        <div className="column is-four-fifths user center">
      <form onSubmit={submitHandler}>
        <div className="notification is-primary">
          <div className="field has-addons">
            <div className="control">
              <input
                onChange={search.onChange}
                className="input search"
                type="text"
                placeholder={`Find a ${media}...`}
              />
              {!media && (
                <strong className="help">Please select a category.</strong>
              )}
            </div>
            <div className="control">
              <button
                disabled={!media && "true"}
                type="submit"
                className="button is-info"
              >
                Search
              </button>
            </div>
          </div>
          <div className="control">
            <label className="radio">
              <input
                onClick={clickHanlder}
                type="radio"
                name="answer"
                value={"movie"}
              />
              Movie
            </label>
            <label className="radio">
              <input
                onClick={clickHanlder}
                type="radio"
                name="answer"
                value={"tv"}
              />
              Tv Show
            </label>
            <label className="radio">
              <input
                onClick={clickHanlder}
                type="radio"
                name="answer"
                value={"users"}
              />
              Users
            </label>
          </div>
        </div>
      </form>
      </div>
      </div>
      {Array.isArray(result) ? (
        <MovieListItem movies={result} />
        ) : (
         <UserProfile user={result} />
          )}
    </>
  );
};

export default SearchBar;
