import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import { FaHeart, FaTrash } from "react-icons/fa";
import { AuthContext } from "../store/context/AuthContext";
import axios from "axios";

const MovieItem = ({ movie }) => {
  const { user } = useContext(AuthContext);
  const userid=useParams().userid

  const addToFavourites = (item) => {
    axios
      .put(`/api/users/${user.id}/add_favourites`, item)
      .then((result)=>console.log(result.data))
      .then(() => alert("Added to your collection successfully"))
      .catch((err) => console.log(err));
  };

  const deleteFromFavourites = (item) => {
    axios
      .put(`/api/users/${user.id}/delete_favourites`, item)
      .then((result)=>console.log(result.data))
      .then(() => alert("Deleted from your collection"))
      .catch((err) => console.log(err));
  };



  return (
    <div class="card">
      <div class="card-image">
        <figure class="has-ratio">
          <img
            src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
            alt="Placeholder image"
          />
        </figure>
      </div>
      <div class="card-content">
        <div class="media">
          <div class="media-left">
            {user&& <FaHeart onClick={() => addToFavourites(movie)} />}
          </div>
          <div class="media-content">
            <p class="title is-4">{movie.original_title}</p>
          </div>
            <p class="subtitle is-6">by Tomi</p>
          <div class="media-right">
            {userid&& <FaTrash onClick={() => deleteFromFavourites(movie)} />}
          </div>
        </div>
        <div class="content">
          {movie.overview}
          <Link
            to={`/single/${movie.first_air_date ? "tv" : "movie"}/${movie.id}`}
          >
            {" "}
            More
          </Link>
          <br />
          <strong>Rank:</strong> <>{movie.vote_average}</>
          <br />
          <time dateTime="2016-1-1">
            <strong>Release Date: </strong>
            {movie.release_date}
          </time>
        </div>
      </div>
    </div>
  );
};

export default MovieItem;
