import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/formHook";
import MovieListItem from "./MovieListItem";



const SearchBar=()=>{
     const search=useInput()
     const [result, setResult]=useState([])

     const submitHandler=(e)=>{
         e.preventDefault()
         axios.get(`api/movies/:${search.value}`)
        // axios.get(`https://api.themoviedb.org/3/search/movie?api_key=22ffb1f173ebcdc0620712cb4161e744&query=${search.value}`)
        // .then((r)=>setResult(r.data.results))
        .catch((err)=>console.log(err))
     }
    console.log(result)
    return(
        <>
      <div class="notification is-primary">
        <div className="field has-addons">
        <div className="control">
          <input {...search} className="input search" type="text" placeholder="Find a Movie" />
        </div>
        <div className="control">
          <a className="button is-info" onClick={submitHandler}>Search</a>
        </div>
      </div>
      </div>
      <MovieListItem movies={result}/>
      </>

    )
}

export default SearchBar
