import axios from "axios";
import React, { useEffect, useContext, useState } from "react";
import { useParams } from "react-router";
import MovieListItem from "../commons/MovieListItem";
import { AuthContext } from "../store/context/AuthContext";


const UserView=()=>{
 const {user}=useContext(AuthContext)
const [movies, setMovies]=useState([])

 useEffect(()=>
    {axios.get(`api/users/${user.id}/collection`)
    .then((r)=>setMovies(r.data))
    .catch((err)=>console.log(err))}
    ,[])

    const addToFavourites = (item) => {
      axios
        .put(`api/users/${user.id}/favourites`, item)
        .then(() => alert("Added to your collection successfully"))
        .catch((err) => console.log(err));
    };

return (
    <>
    <div class="container is-fluid collection">
  <div class="notification movie">
    This  are <strong>{user.userName} favourite's </strong>movies and tv shows. 
  </div>
</div>
      <MovieListItem movies={movies} addToFavourites={addToFavourites}/>
      </>

)
}
export default UserView
